    }))
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <Greetings />
        {/* reusable */}
        <Greeting name="World" age="15" />
        <Timer start="0" />

        <p>Percobaan Button</p>
      </header>
    </div>
  );
}

export default App;
